import React from 'react';
import { Container, Typography, Button } from '@mui/material';
import myImage from '../../../images/1pao.jpg';

const whiteTextStyle = {
  color: '#FFFFFF',
  textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
};

const openInNewTab = (url) => {
  const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
  if (newWindow) newWindow.opener = null;
};

const Home = () => {
  const imageStyle = {
    display: 'block',         // Center the image
    margin: '0 auto',         // Center the image horizontally
    border: '5px solid white', // Add a cool border
    borderRadius: '10px',     // Add border radius for a rounded look
    maxWidth: '100%',         // Ensure the image doesn't exceed its container
  };

  const handleClick = () => {
    openInNewTab('https://barbosapaoloromulus.web.app/');
  };

  return (
    <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '20px' }}>
      {/* Adjust marginTop to move content closer to the navbar */}
      <Typography variant="h3" gutterBottom style={whiteTextStyle}>
        WELCOME TO SEIJVAGE ARTWORKS
      </Typography>
      <Typography variant="h5" paragraph style={whiteTextStyle}>
        {/* Add your text content here */}
      </Typography>
      <Button
        variant="contained"
        color="primary"
        onClick={handleClick}
        style={{ marginBottom: '20px' }} // Add margin to create space between the button and the image
      >
        Check my Portfolio
      </Button>
      <br /> {/* Add a line break */}
      <img src={myImage} alt="My Image" style={imageStyle} /> {/* Add the image here */}
    </Container>
  );
};

export default Home;
