import React, { useEffect, useState } from 'react';
import { Container, Typography, Button } from '@mui/material';

const images = [
  './images/hope.png',
  './images/1pao.jpg',
  './images/2pao.jpg',
  './images/3pao.jpg',
  // Add more relative image paths here
];

const Slideshow = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const nextSlide = () => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    };

    const intervalId = setInterval(nextSlide, 3000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="slideshow">
      {images.map((image, index) => (
        <img
          key={index}
          src={image}
          alt={`Slide ${index + 1}`}
          style={{
            display: index === currentIndex ? 'block' : 'none',
            maxWidth: '100%',
            height: 'auto',
          }}
        />
      ))}
    </div>
  );
};

const LandingPageWithCarousel = () => {
  const whiteTextStyle = {
    color: '#FFFFFF',
    textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  };

  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
    if (newWindow) newWindow.opener = null;
  };

  const handleClick = () => {
    openInNewTab('https://barbosapaoloromulus.web.app/');
  };

  return (
    <div>
      <p style={{ textAlign: "center" }}>DIGITAL ARTWORKS</p>
      <section>
        <style>
          {`.mySlides {
            display: outset;
            background: black;
            padding: 10px;
            border: 1px solid skyblue;
            box-shadow: 10px 10px 10px #00000052;
            position: static;
          }`}
        </style>
        <center>
          <Slideshow />
        </center>
      </section>
      <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '100px' }}>
        <Typography variant="h3" gutterBottom style={whiteTextStyle}>
          WELCOME TO SEIJVAGE ARTWORKS
        </Typography>
        <Typography variant="h5" paragraph style={whiteTextStyle}>
          {/* Your text here */}
        </Typography>
        <Button variant="contained" color="primary" onClick={handleClick}>
          Check my Portfolio
        </Button>
      </Container>
    </div>
  );
};

export default LandingPageWithCarousel;
