import React from 'react';
import { Container, Typography, Card, CardContent, Button } from '@mui/material';
import myImage1 from '../../../images/cswmo.png';
import myImage2 from '../../../images/9.png';
import myImage3 from '../../../images/hope.png';
import myImage4 from '../../../images/cee.png';
import myImage5 from '../../../images/5.png';

const cardStyle = {
  backgroundColor: '#19506e',
  marginBottom: '20px', // Add margin to separate cards
};

const whiteTextStyle = {
  color: '#FFFFFF',
  textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  textAlign: 'justify',
  fontSize: '18px',
};

const xTextStyle = {
  color: '#FFFFFF',
  textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  textAlign: 'justify',
  fontSize: '30px',
  fontWeight: 'bold',
};

const openInNewTab = (url) => {
  const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
  if (newWindow) newWindow.opener = null;
};

const About = () => {
  const imageStyle = {
    display: 'block',
    margin: '0 auto',
    border: 'none',
    borderRadius: '10px',
    maxWidth: '100%',
    width: '450px',
    height: 'auto',
    boxShadow: '0 0 20px white',
  };

  return (
    <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '20px' }}>
      <Typography variant="h3" gutterBottom style={xTextStyle}>
        Learn more about me
      </Typography>
      <Typography variant="h5" paragraph style={whiteTextStyle}>
        A Fullstack Web-Developer and UX/UI designer, I merge my artistic vision and programming expertise in order to transcend my skills in creating an integrated web-application system and graphic design. A mindful, enthusiastic communicator and innovator.
      </Typography>
      <br />
      <div>
        <Card style={cardStyle}>
          <CardContent>
            <img src={myImage1} alt="Image 1" style={{ ...imageStyle, width: '450px', height: 'auto' }} />
            {/* Card content here */}
          </CardContent>
        </Card>
      </div>
      <div>
        <Card style={cardStyle}>
          <CardContent>
            <img src={myImage2} alt="Image 2" style={{ ...imageStyle, width: '450px', height: 'auto' }} />
            {/* Card content here */}
          </CardContent>
        </Card>
      </div>
      <div>
        <Card style={cardStyle}>
          <CardContent>
            <img src={myImage3} alt="Image 3" style={{ ...imageStyle, width: '450px', height: 'auto' }} />
            {/* Card content here */}
          </CardContent>
        </Card>
      </div>
      <div>
        <Card style={cardStyle}>
          <CardContent>
            <img src={myImage4} alt="Image 4" style={{ ...imageStyle, width: '450px', height: 'auto' }} />
            {/* Card content here */}
          </CardContent>
        </Card>
      </div>
      <div>
        <Card style={cardStyle}>
          <CardContent>
            <img src={myImage5} alt="Image 5" style={{ ...imageStyle, width: '450px', height: 'auto' }} />
            {/* Card content here */}
          </CardContent>
        </Card>
      </div>
    </Container>
  );
};

export default About;
