import React, { useState } from 'react';

function BGcolor() {
  const [backgroundColor, setBackgroundColor] = useState('#155371');

  const handleColorChange = () => {
    // Change the background color to a different color
    setBackgroundColor('#ff5733'); // You can use any color code you prefer
  };

  return (
    <div style={{ backgroundColor }}>
      <button onClick={handleColorChange}>Change Background Color</button>
    </div>
  );
}

export default BGcolor;
