import React, { useState } from 'react';
import { Container, Typography, Card, CardContent, Button, Dialog, Grid } from '@mui/material';
import myImage1 from '../../../images/cswmo.png';
import myImage2 from '../../../images/9.png';
import myImage3 from '../../../images/hope.png';
import myImage4 from '../../../images/cee.png';
import myImage5 from '../../../images/5.png';

const cardStyle = {
  backgroundColor: '#19506e',
  marginBottom: '20px', // Add margin to separate cards
  cursor: 'pointer', // Add cursor pointer to indicate the cards are clickable
};

const whiteTextStyle = {
  color: '#FFFFFF',
  textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  textAlign: 'justify',
  fontSize: '18px',
};

const xTextStyle = {
  color: '#FFFFFF',
  textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  textAlign: 'justify',
  fontSize: '30px',
  fontWeight: 'bold',
};

const openInNewTab = (url) => {
  const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
  if (newWindow) newWindow.opener = null;
};

const About = () => {
  const imageStyle = {
    display: 'block',
    margin: '0 auto',
    border: 'none',
    borderRadius: '10px',
    maxWidth: '100%',
    width: '450px',
    height: 'auto',
  };

  const [openModal, setOpenModal] = useState(false);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleOpenModal = (image) => {
    setSelectedImage(image);
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
    setOpenModal(false);
  };

  const cardData = [
    { image: myImage1, title: 'CSWMO WEB BASED SYSTEM', subtitle: 'City Solid Waste Management Office, PPC' },
    { image: myImage2, title: 'Nagtabon Beach', subtitle: 'These were in Photoshop and Ibis Paint X' },
    { image: myImage3, title: 'Hope', subtitle: 'Made in Photoshop' },
    { image: myImage4, title: 'CEE', subtitle: 'College Entrance Exam System' },
    { image: myImage5, title: 'BlackPink 3 Life', subtitle: 'Made in Photoshop' },
  ];

  return (
    <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '20px' }}>
      <Typography variant="h3" gutterBottom style={xTextStyle}>
        Learn more about me
      </Typography>
      <Typography variant="h5" paragraph style={whiteTextStyle}>
        A Fullstack Web-Developer and UX/UI designer, I merge my artistic vision and programming expertise in order to transcend my skills in creating an integrated web-application system and graphic design. A mindful, enthusiastic communicator and innovator.
      </Typography>
      <br />
      <Grid container spacing={2}>
        {cardData.map((item, index) => (
          <Grid item xs={6} key={index}>
            <Card style={cardStyle} onClick={() => handleOpenModal(item.image)}>
              <CardContent>
                <img src={item.image} alt={`Image ${index + 1}`} style={{ ...imageStyle, width: '100%' }} />
                <Typography variant="h6" style={{ color: 'white', marginTop: '10px' }}>
                  {item.title}
                </Typography>
                <Typography variant="body2" style={{ color: 'white' }}>
                  {item.subtitle}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Dialog open={openModal} onClose={handleCloseModal}>
        {selectedImage && (
          <img src={selectedImage} alt="Selected Image" style={{ width: '100%', maxHeight: '80vh' }} />
        )}
      </Dialog>
    </Container>
  );
};

export default About;
