import React, { useEffect, useState } from 'react';
import { Container, Typography, Button } from '@mui/material';

const LandingPageWithCarousel = () => {
  const [myIndex, setMyIndex] = useState(0);

  useEffect(() => {
    const carousel = () => {
      const x = document.getElementsByClassName("mySlides");
      for (let i = 0; i < x.length; i++) {
        x[i].style.display = "none";
      }
      setMyIndex((prevIndex) => (prevIndex + 1 > x.length ? 1 : prevIndex + 1));
      x[myIndex - 1].style.display = "block";
      setTimeout(carousel, 3000);
    };

    const intervalId = setTimeout(carousel, 3000);

    return () => clearTimeout(intervalId);
  }, [myIndex]);

  const showSlides = (n) => {
    const slides = document.getElementsByClassName("mySlides");
    const dots = document.getElementsByClassName("demo");
    const captionText = document.getElementById("caption");

    if (n > slides.length) setMyIndex(1);
    if (n < 1) setMyIndex(slides.length);

    for (let i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }

    for (let i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }

    slides[myIndex - 1].style.display = "block";
    dots[myIndex - 1].className += " active";
    captionText.innerHTML = dots[myIndex - 1].alt;
  };

  const plusSlides = (n) => {
    setMyIndex((prevIndex) => prevIndex + n);
    showSlides(myIndex);
  };

  const currentSlide = (n) => {
    setMyIndex(n);
    showSlides(n);
  };

  const whiteTextStyle = {
    color: '#FFFFFF',
    textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  };

  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
    if (newWindow) newWindow.opener = null;
  };

  const handleClick = () => {
    openInNewTab('https://barbosapaoloromulus.web.app/');
  };

  return (
    <div>
      <p style={{ textAlign: "center" }}>DIGITAL ARTWORKS</p>
      <section>
        <style>
          {`.mySlides {
            display: outset;
            background: black;
            padding: 10px;
            border: 1px solid skyblue;
            box-shadow: 10px 10px 10px #00000052;
            position: static;
          }`}
        </style>
        <center>
          <img className="mySlides" src="./images/hope.png" style={{ width: "97.7%" }} alt="Hope" />
          <img className="mySlides" src="./images/1pao.jpg" style={{ width: "97.7%" }} alt="Image 1" />
          <img className="mySlides" src="./images/2pao.jpg" style={{ width: "97.7%" }} alt="Image 2" />
          <img className="mySlides" src="./images/3pao.jpg" style={{ width: "97.7%" }} alt="Image 3" />
        </center>
      </section>
      <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '100px' }}>
        <Typography variant="h3" gutterBottom style={whiteTextStyle}>
          WELCOME TO SEIJVAGE ARTWORKS
        </Typography>
        <Typography variant="h5" paragraph style={whiteTextStyle}>
          {/* Your text here */}
        </Typography>
        <Button variant="contained" color="primary" onClick={handleClick}>
          Check my Portfolio
        </Button>
      </Container>
    </div>
  );
};

export default LandingPageWithCarousel;
