import React from 'react';
import { Container, Typography, Card, CardContent } from '@mui/material';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import myImage1 from '../../../images/cswmo.png';
import myImage2 from '../../../images/9.png';
import myImage3 from '../../../images/hope.png';
import myImage4 from '../../../images/cee.png';
import myImage5 from '../../../images/5.png';

const cardStyle = {
  backgroundColor: '#12435d', // Change the background color to #12435d
  maxWidth: '400px', // Adjust the maximum width to make the card smaller
  height: '500px', // Adjust the height as desired
};

const whiteTextStyle = {
  color: '#2d5d79',
  textAlign: 'justify',
  fontSize: '18px',
  border: '1px solid white', // Add border style
  padding: '3px', // Add padding to create space between the border and text
};

const xTextStyle = {
  color: '#FFFFFF',
  textShadow: '1px 1px 2px black, 0 0 25px #28628575, 0 0 5px blue',
  textAlign: 'justify',
  fontSize: '30px',
  fontWeight: 'bold',
};

const openInNewTab = (url) => {
  const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
  if (newWindow) newWindow.opener = null;
};

const About = () => {
  const imageStyle = {
    display: 'block',
    margin: '0 auto',
    border: 'none',
    borderRadius: '10px',
    maxWidth: '100%',
    width: '350px', // Adjust the width of the images
    height: 'auto',
    boxShadow: '0 0 20px white',
  };

  return (
    <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '20px' }}>
      <Card style={cardStyle}>
        <CardContent>
          <Typography variant="h3" gutterBottom style={xTextStyle}>
            Learn more about me
          </Typography>
          <Typography variant="h5" paragraph style={whiteTextStyle}>
            A Fullstack Web-Developer and UX/UI designer, I merge my artistic vision and programming expertise in order to transcend my skills in creating an integrated web-application system and graphic design. A mindful, enthusiastic communicator and innovator.
          </Typography>
          <br />
          <Carousel infiniteLoop autoPlay showThumbs={false} interval={3000} showStatus={false}>
            <div>
              <img src={myImage1} alt="Image 1" style={{ ...imageStyle }} />
              {/* Card content here */}
            </div>
            <div>
              <img src={myImage2} alt="Image 2" style={{ ...imageStyle }} />
              {/* Card content here */}
            </div>
            <div>
              <img src={myImage3} alt="Image 3" style={{ ...imageStyle }} />
              {/* Card content here */}
            </div>
            <div>
              <img src={myImage4} alt="Image 4" style={{ ...imageStyle }} />
              {/* Card content here */}
            </div>
            <div>
              <img src={myImage5} alt="Image 5" style={{ ...imageStyle }} />
              {/* Card content here */}
            </div>
          </Carousel>
        </CardContent>
      </Card>
    </Container>
  );
};

export default About;
