import React from 'react';
import { Container, Typography, Button } from '@mui/material';
import ImageSlider from './ImageSlider';

const Home = () => {
  const whiteTextStyle = {
    color: '#FFFFFF',
    textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  };

  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
    if (newWindow) newWindow.opener = null;
  };

  const handleClick = () => {
    openInNewTab('https://barbosapaoloromulus.web.app/');
  };

  // Define your slides and container styles here
  const slides = [
    { url: "http://localhost:3000/image-1.jpg", title: "beach" },
    { url: "http://localhost:3000/image-2.jpg", title: "boat" },
    { url: "http://localhost:3000/image-3.jpg", title: "forest" },
    { url: "http://localhost:3000/image-4.jpg", title: "city" },
    { url: "http://localhost:3000/image-5.jpg", title: "italy" },
  ];

  const containerStyles = {
    width: "500px",
    height: "280px",
    margin: "0 auto",
  };

  return (
    <div>
      <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '100px' }}>
        <Typography variant="h3" gutterBottom style={whiteTextStyle}>
          WELCOME TO SEIJVAGE ARTWORKS
        </Typography>
        <Typography variant="h5" paragraph style={whiteTextStyle}>
          Check my Portfolio
        </Typography>
        <Button variant="contained" color="primary" onClick={handleClick}>
          Get Started
        </Button>
      </Container>

      {/* Include the ImageSlider component with your slides and styles */}
      <div style={containerStyles}>
        <ImageSlider slides={slides} />
      </div>
    </div>
  );
};

export default Home;
