import React from 'react';
import { Container, Typography, Card, CardContent, Button } from '@mui/material';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import the carousel styles
import myImage1 from '../../../images/1pao.jpg';
import myImage2 from '../../../images/2pao.jpg';
import myImage3 from '../../../images/3pao.jpg';

const cardStyle = {
  backgroundColor: '#19506e', // Set the background color to #19506e
};

const whiteTextStyle = {
  color: '#FFFFFF',
  textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
};

const openInNewTab = (url) => {
  const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
  if (newWindow) newWindow.opener = null;
};

const About = () => {
  const imageStyle = {
    display: 'block',
    margin: '0 auto',
    border: 'none',
    borderRadius: '10px',
    maxWidth: '100%',
    width: '450px',
    height: 'auto',
    boxShadow: '0 0 20px white',
  };

  return (
    <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '20px' }}>
      <Typography variant="h3" gutterBottom style={whiteTextStyle}>
       Learn more about me
      </Typography>
      <Typography variant="h5" paragraph style={whiteTextStyle}>
        A Fullstack Web-Developer and UX/UI designer, I merge my artistic vision and programming expertise in order to transcend my skills in creating an integrated web-application system and graphic design. A mindful, enthusiastic communicator and innovator.
      </Typography>
      <br />
      <Carousel infiniteLoop autoPlay showThumbs={false} interval={3000} showStatus={false}>
        <div>
          <Card style={cardStyle}>
            <CardContent>
              <img src={myImage1} alt="Image 1" style={imageStyle} />
              {/* Card content here */}
            </CardContent>
          </Card>
        </div>
        <div>
          <Card style={cardStyle}>
            <CardContent>
              <img src={myImage2} alt="Image 2" style={imageStyle} />
              {/* Card content here */}
            </CardContent>
          </Card>
        </div>
        <div>
          <Card style={cardStyle}>
            <CardContent>
              <img src={myImage3} alt="Image 3" style={imageStyle} />
              {/* Card content here */}
            </CardContent>
          </Card>
        </div>
      </Carousel>
    </Container>
  );
};

export default About;
