import React, { useState, useEffect } from 'react';
import { Container, Typography, Button } from '@mui/material';

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Define image imports
  const images = [
    require('/images/1pao.jpg').default,
    require('/images/2pao.jpg').default,
    require('/images/3pao.jpg').default,
    require('/images/image3.jpg').default,
    require('/images/hope.png').default,
    // Add more image imports here
  ];

  // Function to advance to the next slide
  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  // Function to go to the previous slide
  const prevSlide = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === 0 ? images.length - 1 : prevSlide - 1
    );
  };

  // Automatically advance to the next slide every 5 seconds
  useEffect(() => {
    const interval = setInterval(nextSlide, 5000);
    return () => clearInterval(interval);
  }, []);

  const whiteTextStyle = {
    color: '#FFFFFF',
    textShadow: '1px 1px 2px black, 0 0 25px skyblue, 0 0 5px blue',
  };

  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
    if (newWindow) newWindow.opener = null;
  };

  const handleClick = () => {
    openInNewTab('https://barbosapaoloromulus.web.app/');
  };

  return (
    <div className="Home">
      <div className="slideshow-container">
        {images.map((image, index) => (
          <div
            key={index}
            className={`slide ${index === currentSlide ? 'active' : ''}`}
          >
            <img src={image} alt={`Slide ${index}`} />
          </div>
        ))}

        <button className="prev" onClick={prevSlide}>
          &#10094;
        </button>
        <button className="next" onClick={nextSlide}>
          &#10095;
        </button>
      </div>

      <Container maxWidth="md" style={{ textAlign: 'center', marginTop: '100px' }}>
        <Typography variant="h3" gutterBottom style={whiteTextStyle}>
          WELCOME TO SEIJVAGE ARTWORKS
        </Typography>
        <Typography variant="h5" paragraph style={whiteTextStyle}>
          {/* Add your content here */}
        </Typography>
        <Button variant="contained" color="primary" onClick={handleClick}>
          Check my Portfolio
        </Button>
      </Container>
    </div>
  );
};

export default Home;
